mapper.update:

update refund_picking_details
set refund_picking_id = #{refundPickingId,jdbcType=INTEGER},
  goods_id = #{goodsId,jdbcType=INTEGER},
  quantity = #{quantity,jdbcType=DECIMAL},
  reason = #{reason,jdbcType=VARCHAR}
where refund_picking_details_id = #{refundPickingDetailsId,jdbcType=INTEGER}

mapper.select:

select refund_picking_details_id, refund_picking_id, goods_id, quantity, reason
from refund_picking_details
where refund_picking_details_id = #{refundPickingDetailsId,jdbcType=INTEGER}


select refund_picking_details_id, refund_picking_id, goods_id, quantity, reason,status
from refund_picking_details
WHERE refund_picking_id = #{refundPickingId}


select refund_picking_details_id, refund_picking_id, goods_id, IFNULL(quantity,0) AS quantity, reason,status
from refund_picking_details
where refund_picking_id = #{refundPickingId} and goods_id = #{goodsId}

mapper.update:

update refund_picking_details
set status = 2
where refund_picking_details_id = #{refundPickingDetailsId,jdbcType=INTEGER}
