mapper.insert:


  SELECT LAST_INSERT_ID()

insert into supplier_goods (supplier_id, parts_cd, quantity, 
  product_name, specification, is_delete
  )
values (#{supplierId,jdbcType=INTEGER}, #{partsCd,jdbcType=VARCHAR}, #{quantity,jdbcType=DECIMAL}, 
  #{productName,jdbcType=VARCHAR}, #{specification,jdbcType=VARCHAR}, #{isDelete,jdbcType=TINYINT}
  )

mapper.select:
    select sug.supplier_goods_id, sug.supplier_id, sug.parts_cd, sug.quantity, sug.product_name, sug.specification
    from supplier_goods as sug
	where sug.supplier_id = #{supplierId} and sug.parts_cd = #{partsCd}
